const getDog = async (breedId) => {


    const url = "http://localhost:8000/showAPI";
    const res = await fetch(url);
    const response = await res.json();
    
    function procura(breedId){
    if (response.id === breedId) {
        return response[breedId]
    }}
    const data = response.find(procura(breedId));
    console.log(data)

    const dog = {
	image: data.image_url,
	breed: {
		id: data.id,
		name: data.name,
	    }
    }
    return dog
}

export default getDog;
