import React, { useState, useEffect } from "react";
import getBreeds from "../helpers/getBreeds";

const initialBreeds = [
	{
		id: 1,
		name: "Boxer",
	},
	{
		id: 2,
		name: "Husky",
	},
	{
		id: 3,
		name: "Pintscher",
	},
];

const Select = ({ updateDog }) => {
	const [breeds, setBreeds] = useState(initialBreeds);

	useEffect(() => {
		updateBreeds();
	}, []);

	const updateBreeds = () => {
		getBreeds()
			.then((newBreeds) => {
				setBreeds(newBreeds);
			})
			.catch(console.log("Erro updateBreed"));
	};

	return (
		// <select onChange={(e) => updateDog(e.target.value-1)}>
		// 	{breeds.map((breed) => (
		// 		<option value={breed.id} key={breed.id}>
		// 			{breed.name}
		// 		</option>
		// 	))}
		// </select>

		<div  onChange={(e) => updateDog(e.target.value - 1)}>
			{breeds.map((breed) => (
				<div className='infoIndex'>
					<p>Raça: Affenpinscher</p>
					<p>Tempo de vida médio: 10 - 12 anos</p>
				</div>
			))}
		</div>
	);
};

export default Select;
